import React, { useState, useEffect } from 'react';
import { ChevronDown } from 'lucide-react';
import { getAIProviders } from '../../services/generation';

const AIProviderSelector = ({ selectedProvider, onProviderChange, disabled = false }) => {
    const [providers, setProviders] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        loadProviders();
    }, []);

    const loadProviders = async () => {
        try {
            const response = await getAIProviders();
            setProviders(response.providers);
            if (!selectedProvider && response.providers.length > 0) {
                onProviderChange(response.providers[0]);
            }
        } catch (error) {
            console.error('Failed to load AI providers:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleProviderSelect = (provider) => {
        onProviderChange(provider);
        setIsOpen(false);
    };

    if (loading) {
        return (
            <div className="ai-provider-selector disabled">
                <div className="provider-button">
                    <span>Loading providers...</span>
                    <div className="spinner-small"></div>
                </div>
            </div>
        );
    }

    return (
        <div className={`ai-provider-selector ${disabled ? 'disabled' : ''}`}>
            <label className="provider-label">Select AI Provider</label>

            <div className="provider-dropdown">
                <button className={`provider-button ${isOpen ? 'open' : ''}`} onClick={() => !disabled && setIsOpen(!isOpen)} disabled={disabled}>
                    <div className="provider-info">
                        {selectedProvider ? (
                            <>
                                <div className="provider-icon">
                                    <img src={selectedProvider.icon} alt={selectedProvider.name} />
                                </div>
                                <div className="provider-details">
                                    <span className="provider-name">{selectedProvider.name}</span>
                                    <span className="provider-description">{selectedProvider.description}</span>
                                </div>
                            </>
                        ) : (
                            <span>Select a provider</span>
                        )}
                    </div>
                    <ChevronDown size={20} className={`chevron ${isOpen ? 'rotated' : ''}`} />
                </button>

                {isOpen && (
                    <div className="provider-options">
                        {providers.map(provider => (
                            <div key={provider.id} className={`provider-option ${selectedProvider?.id === provider.id ? 'selected' : ''}`} onClick={() => handleProviderSelect(provider)}>
                                <div className="provider-icon">
                                    <img src={provider.icon} alt={provider.name} />
                                </div>
                                <div className="provider-details">
                                    <span className="provider-name">{provider.name}</span>
                                    <span className="provider-description">{provider.description}</span>
                                    <div className="provider-features">
                                        {provider.features.map(feature => (
                                            <span key={feature} className="feature-tag">{feature}</span>
                                        ))}
                                    </div>
                                </div>
                                {provider.isPremium && <span className="premium-badge">Premium</span>}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default AIProviderSelector;
